<template>
  <div>
    <van-row>
      <van-col span="3" @click="cartvue">
        <span class="iconfont icon-icon_zuo"></span>
      </van-col>
      <van-col span="18">
        <van-nav-bar :title="$route.name" />
      </van-col>
      <van-col span="3">
        <router-link to="/search">
          <span class="iconfont icon-sousuo"></span>
        </router-link>
      </van-col>
    </van-row>
    <!-- 地址 -->
    <div @click="addressfun">
      <van-contact-card
        :type="cardType"
        :name="name"
        :tel="tel"
        add-text="添加联系人"
        :editable="false"
      />
    </div>
    <!-- 商品卡片 -->
