    </van-radio-group>
    <!-- 提交订单 -->
    <van-submit-bar :price="total*100" button-text="提交订单" @submit="onsubmit" v-if="check" />
  </div>
</template>

<script>
export default {
  data() {
    return {
      name: "",
      tel: "",
      radio: 1,
      check: false
    };
  },

  methods: {
    compare() {
      this.check = true;
      this.radio = "1";
    },
    compare2() {
      this.check = true;
      this.radio = "2";
    },
    compare3() {
      this.check = true;
      this.radio = "3";
    },
    compare4() {
      this.check = true;
      this.radio = "4";
    },

    sum() {
      // 总价格计算
      // total 累加值
      this.total = this.lists.reduce((total, item) => {
        // 判断选项选中计算价格
        if (!item.isSelect) return total; //返回默认价格
        return total + parseInt(item.price) * parseInt(item.num); //总价格
      }, 0); //总价格默认值0
    },
    // 定义跳转方法
    addressfun() {
      // 如果地址没有数据，直接跳转地址添加页面
      if (this.$store.state.address.lists.length == 0) {
        this.$router.push("/addressAdd");
      } else {
        this.$router.push("/address");
      }
    },
    cartvue() {
      // 保存订单数据
      let order = {
        orderId: new Date().getTime(),
        address: this.address,
        goodsList: this.lists,
        dateTime: new Date(),
        type: 1 //未支付
      };
      // 调用vuex订单数据状态
      this.$store.commit("order/add", order);
      // 清空购物车
      this.$store.state.cart.lists = [];
      this.$store.state.cart.num = 0;
      this.$router.push("/cart");
      this.$store.state.vanTabbar = true;
    },
    // 提交订单
    onsubmit() {
      // 保存订单数据
      let order = {
        orderId: new Date().getTime(),
        address: this.address,
        goodsList: this.lists,
        dateTime: new Date(),
        type: 2 //已支付
      };
      // 调用vuex订单数据状态
      this.$store.commit("order/add", order);
      // 清空购物车
      this.$store.state.cart.lists = [];
      this.$store.state.cart.num = 0;
      this.$router.push("/orderList/orderAll");
    }
  },
  computed: {
    cardType() {
      return this.$store.state.address.lists.length == 0 ? "add" : "edit";
    }
  },
  created() {
    // 获取地址数据
    let data = this.$store.state.address.lists.find(item => item.isDefault);
    if (data) {
      this.name = data.name;
      this.tel = data.tel;
      this.address = data; //定义地址对象
    }

    this.lists = this.$store.state.cart.lists;
    this.sum(); //计算总价格
    //隐藏导航
    this.$store.state.vanTabbar = false;
  },
  beforeDestroy() {
    this.$store.state.vanTabbar = true;
  }
};
</script>
